	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (1, N'James Lendall Basford', N'The man who never has money enough to pay his debts has too much of something else.', '20140516 20:21:08.107', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (2, N'Adam Smith', N'What can be added to the happiness of a man who is in health, out of debt, and has a clear conscience?', '20140516 20:21:41.223', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (3, N'Ogden Nash', N'Some debts are fun when you are acquiring them, but none are fun when you set about retiring them.', '20140516 20:24:05.943', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (4, N'Unknown', N'The only man who sticks closer to you in adversity than a friend is a creditor.', '20140516 20:24:37.987', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (5, N'Unknown', N'Running into debt isn’t so bad. It’s running into creditors that hurts.', '20140516 20:25:30.117', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (6, N'Proverbs 22:7', N'The rich rules over the poor, and the borrower is the slave of the lender.', '20140516 20:26:00.760', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (7, N'Benjamin Franklin', N'Rather go to bed supperless, than rise in debt.', '20140516 20:28:09.867', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (8, N'Dave Ramsey', N'Debt is normal. Be weird.', '20140516 20:28:27.640', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (9, N'Publilius Syrus', N'Debt is the slavery of the free.', '20140516 21:25:32.157', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (10, N'Andrew Jackson', N'When you get in debt you become a slave.', '20140516 21:26:23.397', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (11, N'Benjamin Franklin', N'Tis against some mens principle to pay interest, and seems against others interest to pay the principle.', '20140516 21:26:58.750', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (12, N'Thomas Jefferson', N'Never spend your money before you have it.', '20140516 21:27:23.630', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (13, N'Earl Wilson', N'If you think nobody cares if you’re alive, try missing a couple of car payments.', '20140516 21:28:33.020', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (14, N'Earl Wilson', N'This would be a much better world if more married couples were as deeply in love as they are in debt.', '20140516 21:29:04.677', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (15, N'Ambrose Bierce', N'Debt, n.  An ingenious substitute for the chain and whip of the slavedriver.', '20140516 21:29:42.017', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (16, N'Ralph Waldo Emerson', N'Wouldst thou shut up the avenues of ill, Pay every debt as if God wrote the bill.', '20140516 21:32:11.560', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (17, N'William Shakespeare', N'Neither a borrower nor a lender be; for loan doth oft lose both itself and friend.', '20140516 21:32:35.897', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (18, N'Henry Wadsworth Longfellow', N'He looks the whole world in the face for he owes not any man.', '20140516 21:33:21.060', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (19, N'Thomas Fuller', N'Debt is the worst poverty.', '20140516 21:33:50.890', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (20, N'Edward G. Bulwer-Lytton', N'Youth is in danger until it learns to look upon debts as furies.', '20140516 21:34:20.533', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (21, N'Robert Green Ingersoll', N'A mortgage casts a shadow on the sunniest field.', '20140516 21:37:21.330', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (22, N'Ezra Pound', N'Wars in old times were made to get slaves. The modern implement of imposing slavery is debt.', '20140516 21:38:00.823', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (23, N'Proverbs 13:11', N'Dishonest money dwindles away, but whoever gathers money little by little makes it grow. ', '20170518 10:08:00.000', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (24, N'Hebrews 13:5', N'Keep your lives free from the love of money and be content with what you have, because God has said, “Never will I leave you; never will I forsake you.” ', '20170518 10:10:00.000', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (25, N'Proverbs 10:4', N'Lazy hands make for poverty, but diligent hands bring wealth. ', '20170518 10:12:00.000', N'James')
	INSERT INTO [PublicContent].[Quotes] ([ID], [Source], [Text], [CreatedOn], [CreatedBy]) VALUES (26, N'Luke 14:28', N'Suppose one of you wants to build a tower. Won’t you first sit down and estimate the cost to see if you have enough money to complete it? For if you lay the foundation and are not able to finish it, everyone who sees it will ridicule you.', '20170518 10:14:00.000', N'James')
